import React from 'react'
import { Link } from 'react-router-dom'

import '../../App.css'

import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import Typography from '@mui/material/Typography';
import Button from '@mui/material/Button';
import Multiselect from 'multiselect-react-dropdown';
import { withTheme } from '@emotion/react';

function ButtonAppBar() {
    return (
      <Box sx={{ flexGrow: 1 }}>
        <AppBar position="fixed">
          <Toolbar>
            <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
              <a className="header-link" href="/" color="inherit">CERTIFYDE</a>
            </Typography>
            <Link className="header-link" color="inherit" to="/login">
                <Button className="header-link" color="inherit">Login</Button>
            </Link>
            <Link className="header-link" color="inherit" to="/register">
                <Button className="header-link" color="inherit">Sign up</Button>
            </Link>
          </Toolbar>
        </AppBar>
      </Box>
    );
  }

export default function SignUpPage() {

    return (
        <><ButtonAppBar /><div className="text-center m-5-auto">
            <h2>Join us</h2>
            <h5>Create your personal account</h5>
            <form action="/home">
                <p>
                    <label>Username</label><br />
                    <input type="text" name="first_name" required />
                </p>
                <p>
                    <label>Email address</label><br />
                    <input type="email" name="email" required />
                </p>
                <p>
                    <label>Password</label><br />
                    <input type="password" name="password" requiredc />
                </p>
                <p>
                    <label>Preferences</label><br />
                    <Multiselect
                        className='multi_select'
                        isObject={false}
                        onKeyPressFn={function noRefCheck(){}}
                        onRemove={function noRefCheck(){}}
                        onSearch={function noRefCheck(){}}
                        onSelect={function noRefCheck(){}}
                        options={[
                            'Option 1',
                            'Option 2',
                            'Option 3',
                            'Option 4',
                            'Option 5'
                        ]}
                        placeholder=""
                        style={{
                            chips: {
                                background: 'white'
                            },
                            multiselectContainer: {
                                color: 'white'
                            },
                            searchBox: {
                                'background': 'white',
                                'width': '15rem',
                                'padding': '.3rem',
                                'border-radius': '5px',
                                'outline': 'none',
                                'border': 'none'
                            }
                        }}
                    />
                </p>
                <p>
                    <button id="sub_btn" type="submit">Register</button>
                </p>
            </form>
            <footer>
                <p><Link to="/">Back to Homepage</Link>.</p>
            </footer>
        </div></>
    )

}
